mapper.select:
WITH time_range AS ( SELECT #{queryBegin}::timestamp AS begin_time, #{queryEnd}::timestamp AS end_time ), patrol_cycles AS ( SELECT l.id AS line_id, l.linename, l.linenum, l.zzid, l.pointids, l.begintime, l.cycle, ARRAY_LENGTH(l.pointids, 1) AS total_points, gs.cycle_index AS cycle_index, GREATEST(l.begintime, trr.begin_time) + (gs.cycle_index * (l.cycle || ' hours')::interval) AS cycle_start_time, GREATEST(l.begintime, trr.begin_time) + ((gs.cycle_index + 1) * (l.cycle || ' hours')::interval) - interval '1 second'AS cycle_end_time FROM nfcpatrol.t_nfcpatrol_line l CROSS JOIN time_range trr CROSS JOIN LATERAL ( SELECT generate_series( 0, FLOOR(EXTRACT(EPOCH FROM (LEAST(trr.end_time, now()) - GREATEST(l.begintime, trr.begin_time))) / (l.cycle * 3600))::int ) AS cycle_index ) gs WHERE l.deleted = false AND ARRAY_LENGTH(l.pointids, 1) != 0 AND (l.begintime::timestamp) <= (trr.end_time::timestamp)
AND zzid IN
#{oneid}
), cycle_patrol_stats AS ( SELECT pc.line_id, pc.linename, pc.linenum, pc.zzid, pc.cycle, pc.cycle_index, pc.cycle_start_time, pc.cycle_end_time, pc.total_points, COUNT(DISTINCT last_records.rfid) AS checked_points, STRING_AGG(DISTINCT last_records.person, ', ') AS patrol_persons, COALESCE( ( SELECT r.deptid FROM nfcpatrol.t_nfcpatrol_record r WHERE r.rfid = ANY(pc.pointids) AND r.dotime BETWEEN pc.cycle_start_time AND pc.cycle_end_time ORDER BY r.dotime DESC LIMIT 1), '/' ) AS last_deptid FROM patrol_cycles pc LEFT JOIN LATERAL ( SELECT DISTINCT ON (r.rfid) r.rfid, r.person, r.errornum, r.dotime, r.deptid FROM nfcpatrol.t_nfcpatrol_record r WHERE r.rfid = ANY(pc.pointids) AND r.dotime BETWEEN pc.cycle_start_time AND pc.cycle_end_time ORDER BY r.rfid, r.dotime DESC ) last_records ON true GROUP BY pc.line_id, pc.linename, pc.linenum, pc.zzid, pc.cycle, pc.cycle_index, pc.cycle_start_time, pc.cycle_end_time, pc.total_points, pc.pointids ), final_result AS ( SELECT cps.line_id as lineid, cps.linename as linename, cps.zzid as zzid, cps.cycle as cycle, cps.last_deptid as deptid, COALESCE(d.name, '/') AS deptname, cps.cycle_start_time as cycle_start_time, cps.cycle_end_time as cycle_end_time, COALESCE(cps.patrol_persons, '/') AS patrol_person, cps.checked_points AS checked_cnt, cps.total_points AS total_cnt, CASE WHEN CURRENT_TIMESTAMP BETWEEN cps.cycle_start_time AND cps.cycle_end_time THEN CASE WHEN cps.checked_points = 0 THEN 1 WHEN cps.checked_points != 0 AND cps.checked_points < cps.total_points THEN 2 WHEN cps.checked_points >= cps.total_points THEN 3 END
WHEN cps.cycle_end_time < CURRENT_TIMESTAMP THEN CASE WHEN cps.checked_points = 0 THEN 4 WHEN cps.checked_points != 0 AND cps.checked_points < cps.total_points THEN 5 WHEN cps.checked_points >= cps.total_points THEN 3 END ELSE 6 END AS status FROM cycle_patrol_stats cps LEFT JOIN nfcpatrol.t_nfcpatrol_dept d ON d.id = cps.last_deptid )
select * from final_result
status = ${queryByStatus}
AND (deptid IN
#{oneid}
or deptid = '/')
order by linename, cycle_start_time desc
